

sql= "update ce_inventorymonitor_items set usageunitamountperpackage = (select packagenum from CE_SysDir_Drugs  where systemdrugid in (select standarditemcode from Ce_kb_medicalitemmappings where localitemcode in(select ce_socialinsdir_drugs_id from ce_socialinsdir_drugs where nationaldrugcode in ('86900847000037')))and rownum<2) ,(packageunitrefval,packageunitrefname)= (select biztypeitemvalue,biztypeitemname from basic_bifinitions where dictionarycode='YP0030' and biztypeitemname in (SELECT packageunit from ce_socialinsdir_drugs where CE_SocialInsDir_Drugs_id in (SELECT localitemcode from CE_KB_MedicalItemMappings where standarditemcode in (SELECT systemdrugid from CE_SysDir_Drugs where nationaldrugcode in('86900847000037'))) ) ),(usageunitrefval,usageunitrefname,pdunitrefname) = (select biztypeitemvalue,biztypeitemname,biztypeitemname from basic_bifinitions where dictionarycode='YP0031' and biztypeitemname in (SELECT loadcapacityunit from ce_socialinsdir_drugs where CE_SocialInsDir_Drugs_id in (SELECT localitemcode from CE_KB_MedicalItemMappings where standarditemcode in (SELECT systemdrugid from CE_SysDir_Drugs where nationaldrugcode in('86900847000037'))) )),drugspec = (select drugspec from ce_socialinsdir_drugs where nationaldrugcode ='86900847000037'),add_userid='administrator',(institutionname,institutioncode)=(select institutionname,institutioncode from CE_BASIC_MEDICALIONS where institutioncode='181220'),add_date=(select sysdate from dual),ce_inventorymonitor_items_id='zh'||ce_inventorymonitor_items_id,drughiscode='1906'   where nationaldrugcode='86900847000037'; "


insert="insert into ce_inventorymonitor_items(districtid,districtcode,nationaldrugcode,genericname,productionname,productionunit,packageunitrefcode,usageunitrefcode,ce_inventorymonitor_items_id) select districtid,510181,nationaldrugcode,genericname,productionname,productionunit,'YP0030','YP0031',( SELECT (SYSDATE - TO_DATE('1970-1-1 8', 'YYYY-MM-DD HH24')) * 86400 FROM DUAL) from ce_socialinsdir_drugs where nationaldrugcode='86900847000037' ;"


